import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { getStudentPrompts } from '../../services/api';
import { createAvatar } from '@dicebear/core';
import { bottts } from '@dicebear/collection';
import { FaSpinner, FaComments, FaSearch, FaSortAlphaDown, FaSortAlphaUp } from 'react-icons/fa';
import ChatInterface from './ChatInterface';
import '../../styles/StudentDashboard.css';

function useGamification() {
  // simple, local gamification counters; backend-safe
  const [xp, setXp] = useState(() => parseInt(localStorage.getItem('student_xp') || '0', 10));
  const [streak, setStreak] = useState(() => parseInt(localStorage.getItem('student_streak') || '0', 10));
  const [lastActive, setLastActive] = useState(() => localStorage.getItem('student_last_active') || '');

  useEffect(() => {
    const today = new Date().toISOString().slice(0,10);
    if (lastActive !== today) {
      // new day â€” bump streak if yesterday was last active
      const y = new Date(Date.now() - 86400000).toISOString().slice(0,10);
      const newStreak = lastActive === y ? streak + 1 : 1;
      setStreak(newStreak);
      setLastActive(today);
      localStorage.setItem('student_streak', String(newStreak));
      localStorage.setItem('student_last_active', today);
    }
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const addXp = useCallback((amount) => {
    const v = Math.max(0, xp + amount);
    setXp(v);
    localStorage.setItem('student_xp', String(v));
    const today = new Date().toISOString().slice(0,10);
    localStorage.setItem('student_last_active', today);
  }, [xp]);

  const level = Math.floor(xp / 100) + 1;
  const levelProgress = (xp % 100) / 100; // 0..1

  return { xp, addXp, streak, level, levelProgress };
}

function AssistantViewStudent() {
  const [selectedPrompt, setSelectedPrompt] = useState(null);
  const [availablePrompts, setAvailablePrompts] = useState([]);
  const [isLoadingPrompts, setIsLoadingPrompts] = useState(true);
  const [error, setError] = useState('');
  const [assistantAvatars, setAssistantAvatars] = useState({});
  const [search, setSearch] = useState('');
  const [sortAsc, setSortAsc] = useState(true);

  const { xp, streak, level, levelProgress } = useGamification();

  const showError = useCallback((msg) => setError(msg), []);

  const fetchPrompts = useCallback(async () => {
    setIsLoadingPrompts(true); setError('');
    try {
      const response = await getStudentPrompts();
      const promptsData = response.data || [];
      if (!Array.isArray(promptsData)) throw new Error("Invalid data format received from server.");
      setAvailablePrompts(promptsData);

      const avatars = {};
      promptsData.forEach(prompt => {
        if (prompt && prompt.id) {
          const avatar = createAvatar(bottts, { seed: prompt.id, size: 40 });
          avatars[prompt.id] = avatar.toString();
        }
      });
      setAssistantAvatars(avatars);
    } catch (err) {
      console.error("Error fetching student prompts:", err);
      showError("Could not load available assistants. Please refresh.");
      setAvailablePrompts([]);
    } finally {
      setIsLoadingPrompts(false);
    }
  }, [showError]);

  useEffect(() => { fetchPrompts(); }, [fetchPrompts]);

  const filtered = useMemo(() => {
    const term = search.trim().toLowerCase();
    let list = availablePrompts;
    if (term) {
      list = list.filter(p =>
        (p.name || '').toLowerCase().includes(term) ||
        (p.description || '').toLowerCase().includes(term)
      );
    }
    list = [...list].sort((a, b) => {
      const A = (a.name || '').toLowerCase();
      const B = (b.name || '').toLowerCase();
      return sortAsc ? A.localeCompare(B) : B.localeCompare(A);
    });
    return list;
  }, [availablePrompts, search, sortAsc]);

  const handleSelectPrompt = (prompt) => setSelectedPrompt(prompt);
  const handleBackToList = () => setSelectedPrompt(null);

  return (
    <div className='assistant-view-container'>

      {/* Gamified strip */}
      {!selectedPrompt && (
        <div className="gamify-strip widget">
          <div className="gamify-card level">
            <div className="ring">
              {/* progress ring via conic-gradient in CSS, driven by inline var */}
              <div className="ring-track" style={{ ['--ring-progress']: `${Math.round(levelProgress * 100)}%` }} />
              <div className="ring-center">
                <span>Lv</span>
                <strong>{level}</strong>
              </div>
            </div>
            <div className="meta">
              <h4>Your Level</h4>
              <p>{Math.round(levelProgress*100)}% to next</p>
            </div>
          </div>

          <div className="gamify-card streak">
            <div className="big-num">{streak}</div>
            <div className="meta">
              <h4>Day Streak</h4>
              <p>Keep it going!</p>
            </div>
          </div>

          <div className="gamify-card xp">
            <div className="big-num">{xp}</div>
            <div className="meta">
              <h4>Total XP</h4>
              <p>Earn by learning</p>
            </div>
          </div>
        </div>
      )}

      {error && <div className="message error-message global-message">{error}</div>}

      {!selectedPrompt ? (
        <div className="select-assistant-view">
          <div className='page-header' style={{ borderBottom: 'none', marginBottom: '1rem'}}>
            <h2><FaComments/> Select an AI Assistant</h2>
          </div>

          {/* toolbar */}
          <div className="toolbar-row">
            <div className="input-with-icon">
              <FaSearch />
              <input
                type="text"
                placeholder="Search assistants..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                aria-label="Search assistants"
              />
            </div>
            <button className="button subtle-button" onClick={() => setSortAsc(s => !s)} title="Toggle sort">
              {sortAsc ? <><FaSortAlphaDown/> Aâ†’Z</> : <><FaSortAlphaUp/> Zâ†’A</>}
            </button>
          </div>

          {isLoadingPrompts && (
            <div className='items-grid'>
              {[...Array(6)].map((_, i) => (
                <div key={i} className="item-card skeleton">
                  <div className="s-line wide" />
                  <div className="s-line" />
                  <div className="s-line short" />
                </div>
              ))}
            </div>
          )}

          {!isLoadingPrompts && filtered.length === 0 && (
            <p className='no-items-message'>No AI Assistants match your search.</p>
          )}

          {!isLoadingPrompts && filtered.length > 0 && (
            <div className='items-grid'>
              {filtered.map(prompt => (
                <div
                  key={prompt.id}
                  className="item-card assistant-card hover-raise"
                  onClick={() => handleSelectPrompt(prompt)}
                  title="Click to start chat"
                >
                  <div className="item-card-header">
                    <div className="avatar item-card-icon" dangerouslySetInnerHTML={{ __html: assistantAvatars[prompt.id] || '' }} />
                    <h3 className="item-card-title">{prompt.name || 'Unnamed Assistant'}</h3>
                  </div>
                  <p className="item-card-description">{prompt.description || 'A helpful assistant.'}</p>
                  <div className='tag-row'>
                    <span className="tag">Interactive</span>
                    <span className="tag">Study helper</span>
                  </div>
                  <div className='item-card-footer'>
                    <button className='button primary-button' onClick={(e) => { e.stopPropagation(); handleSelectPrompt(prompt); }}>Chat Now</button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      ) : (
        <ChatInterface
          promptId={selectedPrompt.id}
          promptName={selectedPrompt.name}
          promptDescription={selectedPrompt.description}
          assistantAvatarSvg={assistantAvatars[selectedPrompt.id]}
          onBack={handleBackToList}
        />
      )}
    </div>
  );
}

export default AssistantViewStudent;
