import React, { useState, useRef, useEffect } from 'react';
import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import { FaGripVertical, FaTrash, FaEdit, FaCheck, FaTimes } from 'react-icons/fa'; // Icons
import { BlockTypes } from '../PromptWorkshop'; // Import BlockTypes
import '../../../styles/PromptWorkshop.css'; // Ensure CSS is created/updated

function SortableBlock({ id, block, onDeleteBlock, onUpdateBlockContent, isDragging, isOpacityReduced }) {
    const [isEditing, setIsEditing] = useState(block.type === BlockTypes.CUSTOM_TEXT && block.content.includes('Enter your custom instruction')); // Start editing if it's a new custom block
    const [editedContent, setEditedContent] = useState(block.content);
    const textareaRef = useRef(null); // Ref for the textarea

    const {
        attributes,
        listeners,
        setNodeRef,
        transform,
        transition,
        isDragging: isSortableDragging // Renamed to avoid conflict
    } = useSortable({ id: id });

    // Combine isDragging props if needed
    const currentlyDragging = isDragging || isSortableDragging;

    const style = {
        transform: CSS.Transform.toString(transform),
        transition: transition || undefined, // Ensure transition is not null
        opacity: isOpacityReduced ? 0.5 : 1,
        cursor: currentlyDragging ? 'grabbing' : 'grab',
        zIndex: currentlyDragging ? 10 : 'auto', // Bring dragged item to front
    };

    // --- Edit Handling ---
    const handleEditClick = () => {
        setEditedContent(block.content); // Reset edit buffer to current content
        setIsEditing(true);
    };

    const handleSaveEdit = () => {
        if (editedContent.trim() !== block.content) {
            onUpdateBlockContent(id, editedContent.trim());
        }
        setIsEditing(false);
    };

    const handleCancelEdit = () => {
        setEditedContent(block.content); // Revert changes
        setIsEditing(false);
    };

    const handleContentChange = (e) => {
        setEditedContent(e.target.value);
        // Auto-resize textarea
        if (textareaRef.current) {
            textareaRef.current.style.height = 'auto'; // Reset height
            textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`;
        }
    };

     // Auto-focus and resize textarea when editing starts
    useEffect(() => {
        if (isEditing && textareaRef.current) {
            textareaRef.current.focus();
            textareaRef.current.select(); // Select text for easy replacement
             // Adjust height initially and in case content was loaded
            textareaRef.current.style.height = 'auto';
            textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`;
        }
    }, [isEditing]);


    // Determine block type for styling/iconography
    const getBlockIcon = () => {
        switch(block.type) {
            case BlockTypes.MATERIAL_SUMMARY: return <span title="Material Summary">📄</span>;
            case BlockTypes.CUSTOM_TEXT: return <span title="Custom Text">✏️</span>;
            case BlockTypes.PALETTE_ITEM: // Should be CANVAS_BLOCK on canvas
            case BlockTypes.CANVAS_BLOCK:
                 // Heuristic for icons based on title/content
                if (block.title?.includes('Task')) return <span title={block.title}>🎯</span>;
                if (block.title?.includes('Tone')) return <span title={block.title}>🗣️</span>;
                if (block.title?.includes('Context')) return <span title={block.title}>📚</span>;
                if (block.title?.includes('Guiding') || block.title?.includes('Step')) return <span title={block.title}>❓</span>;
                if (block.title?.includes('Example')) return <span title={block.title}>✅</span>;
                if (block.title?.includes('Safety')) return <span title={block.title}>🛡️</span>;
                return <span title="Instruction">⚙️</span>; // Default instruction icon
            default: return <span title="Block">🧱</span>;
        }
    };

    return (
        <div ref={setNodeRef} style={style} className={`canvas-block ${currentlyDragging ? 'dragging' : ''} type-${block.type || 'default'}`}>
             {/* Drag Handle (only visible when not editing) */}
            {!isEditing && (
                <button {...attributes} {...listeners} className="drag-handle subtle-button" title="Drag to reorder">
                    <FaGripVertical />
                </button>
            )}

            <div className="block-content-area">
                 <div className="block-header">
                    {getBlockIcon()}
                    <strong>{block.title || 'Instruction Block'}</strong>
                 </div>
                {isEditing ? (
                    <textarea
                        ref={textareaRef}
                        value={editedContent}
                        onChange={handleContentChange}
                        onBlur={handleSaveEdit} // Save when focus is lost
                        rows={3} // Start with a few rows
                        className="block-textarea"
                    />
                ) : (
                    <p className="block-text" onDoubleClick={block.type === BlockTypes.CUSTOM_TEXT ? handleEditClick : undefined}>
                         {block.content}
                         {block.type === BlockTypes.CUSTOM_TEXT && <FaEdit className="edit-hint-icon" title="Double-click to edit"/>}
                    </p>
                )}
            </div>

            {/* Action Buttons */}
            <div className="block-actions">
                {isEditing ? (
                    <>
                        <button onClick={handleSaveEdit} className="action-button save subtle-button" title="Save changes">
                            <FaCheck />
                        </button>
                        <button onClick={handleCancelEdit} className="action-button cancel subtle-button" title="Cancel edit">
                            <FaTimes />
                        </button>
                    </>
                ) : (
                    <>
                     {/* Allow editing for custom text blocks */}
                     {block.type === BlockTypes.CUSTOM_TEXT && (
                        <button onClick={handleEditClick} className="action-button edit subtle-button" title="Edit content">
                             <FaEdit />
                         </button>
                     )}
                        <button onClick={() => onDeleteBlock(id)} className="action-button delete subtle-button" title="Delete block">
                            <FaTrash />
                        </button>
                    </>
                )}
            </div>
        </div>
    );
}

export default SortableBlock;
