import React, { useState, useRef, useEffect, useMemo } from 'react';
import { askAssistant } from '../../services/api';
import { FaPaperPlane, FaSpinner, FaArrowLeft } from 'react-icons/fa';
import { createAvatar } from '@dicebear/core';
import { adventurer, bottts } from '@dicebear/collection';
import { useAuth } from '../../contexts/AuthContext';
import '../../styles/StudentDashboard.css';

const UserAvatar = () => {
  const { user } = useAuth();
  const svg = useMemo(() => {
    if (!user?.email) return '';
    return createAvatar(adventurer, { seed: user.email, size: 36, radius: 50 }).toString();
  }, [user]);
  return (
    <div className="message-avatar-container user-avatar">
      <div className="avatar" dangerouslySetInnerHTML={{ __html: svg }} title="Εσείς" />
    </div>
  );
};

const AssistantAvatar = ({ svgString }) => (
  <div className="message-avatar-container assistant-avatar">
    <div className="avatar" dangerouslySetInnerHTML={{ __html: svgString }} title="Βοηθός AI" />
  </div>
);

const SUGGESTIONS = [
  "Εξήγησέ το σαν να είμαι 12",
  "Δώσε μου μια γρήγορη περίληψη",
  "Δείξε μου τα βήματα ένα-ένα",
  "Φτιάξε μια ερώτηση εξάσκησης",
  "Ποια είναι τα συνήθη λάθη;",
];

function ChatInterface({ promptId, promptName, promptDescription, assistantAvatarSvg, onBack }) {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const chatEndRef = useRef(null);

  useEffect(() => { chatEndRef.current?.scrollIntoView({ behavior: 'smooth' }); }, [messages, isLoading]);
  useEffect(() => { setMessages([]); setError(''); setInputMessage(''); setIsLoading(false); }, [promptId]);

  const defaultAssistantAvatar = useMemo(
    () => assistantAvatarSvg || createAvatar(bottts, { seed: promptId, size: 40 }).toString(),
    [assistantAvatarSvg, promptId]
  );

  const handleSendMessage = async (e) => {
    if (e) e.preventDefault();
    const userMessageContent = inputMessage.trim();
    if (!userMessageContent || isLoading) return;

    setError('');
    const newUserMessage = { role: 'user', content: userMessageContent };
    setMessages(prev => [...prev, newUserMessage]);
    setInputMessage('');
    setIsLoading(true);

    try {
      const response = await askAssistant(promptId, userMessageContent);
      const assistantMessage = { role: 'assistant', content: response.data.response };
      setMessages(prev => [...prev, assistantMessage]);
    } catch (err) {
      console.error("Error asking assistant:", err);
      const errorMessage = err.response?.data?.error || "Συγγνώμη, δεν μπόρεσα να λάβω απάντηση.";
      setError(errorMessage);
      setMessages(prev => [...prev, { role: 'assistant', content: `Σφάλμα: ${errorMessage}` }]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSuggestionClick = (suggestion) => {
    setInputMessage(suggestion);
    // Simulate form submission
    handleSendMessage(null);
  };

  return (
    <div className="chat-interface-container widget">
      <div className="chat-header">
        {onBack && (
          <button onClick={onBack} className='subtle-button back-button' title="Επιστροφή στη λίστα" aria-label="Επιστροφή στη λίστα">
            <FaArrowLeft />
          </button>
        )}
        <div className='avatar' dangerouslySetInnerHTML={{ __html: defaultAssistantAvatar }}/>
        <div className="chat-header-info">
          <h3>{promptName || 'Βοηθός AI'}</h3>
          <p>{promptDescription || 'Έτοιμος να βοηθήσω!'}</p>
        </div>
      </div>

      {messages.length === 0 && !isLoading && (
        <div className="chat-suggestions">
          <p className="hint">Δοκίμασε μία από τις παρακάτω προτάσεις για να ξεκινήσεις:</p>
          <div className="chips">
            {SUGGESTIONS.map((s) => (
              <button key={s} className="chip" onClick={() => handleSuggestionClick(s)}>{s}</button>
            ))}
          </div>
        </div>
      )}

      <div className="chat-messages">
        {messages.map((msg, index) => (
          <div key={index} className={`message-bubble ${msg.role}`}>
            {msg.role === 'user' ? <UserAvatar /> : <AssistantAvatar svgString={defaultAssistantAvatar} />}
            <div className="message-content">
              <pre>{msg.content}</pre>
            </div>
          </div>
        ))}
        {isLoading && (
          <div className="message-bubble assistant loading">
            <AssistantAvatar svgString={defaultAssistantAvatar}/>
            <div className="message-content typing">
              <span className="dot" /><span className="dot" /><span className="dot" />
            </div>
          </div>
        )}
        <div ref={chatEndRef} />
      </div>

      {error && <p className="error-message chat-error">{error}</p>}

      <form className="chat-input-form" onSubmit={handleSendMessage}>
        <input
          type="text"
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          placeholder="Κάνε την ερώτησή σου..."
          disabled={isLoading}
          aria-label="Εισαγωγή μηνύματος"
        />
        <button type="submit" disabled={isLoading || !inputMessage.trim()} title="Αποστολή">
          {isLoading ? <FaSpinner className="spin" /> : <FaPaperPlane />}
        </button>
      </form>
    </div>
  );
}

export default ChatInterface;
