import React, { useState, useRef, useEffect } from 'react';
import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import { FaGripVertical, FaTrash, FaEdit, FaCheck, FaTimes } from 'react-icons/fa'; // Icons
import { BlockTypes } from '../PromptWorkshop'; // Import BlockTypes
import '../../../styles/PromptWorkshop.css'; // Ensure CSS is created/updated

function SortableBlock({ id, block, onDeleteBlock, onUpdateBlockContent, isDragging, isOpacityReduced }) {
    // ŒàŒΩŒ±œÅŒæŒ∑ ŒµœÄŒµŒæŒµœÅŒ≥Œ±œÉŒØŒ±œÇ ŒµŒ¨ŒΩ ŒµŒØŒΩŒ±Œπ ŒΩŒ≠Œø œÄœÅŒøœÉŒ±œÅŒºŒøœÉŒºŒ≠ŒΩŒø ŒºœÄŒªŒøŒ∫
    const [isEditing, setIsEditing] = useState(block.type === BlockTypes.CUSTOM_TEXT && block.content.includes('ŒïŒπœÉŒ±Œ≥Œ¨Œ≥ŒµœÑŒµ œÑŒ∑ Œ¥ŒπŒ∫ŒÆ œÉŒ±œÇ ŒøŒ¥Œ∑Œ≥ŒØŒ±...'));
    const [editedContent, setEditedContent] = useState(block.content);
    const textareaRef = useRef(null); // Ref for the textarea

    const {
        attributes,
        listeners,
        setNodeRef,
        transform,
        transition,
        isDragging: isSortableDragging // Renamed to avoid conflict
    } = useSortable({ id: id });

    // Combine isDragging props if needed
    const currentlyDragging = isDragging || isSortableDragging;

    const style = {
        transform: CSS.Transform.toString(transform),
        transition: transition || undefined, // Ensure transition is not null
        opacity: isOpacityReduced ? 0.5 : 1,
        cursor: currentlyDragging ? 'grabbing' : 'grab',
        zIndex: currentlyDragging ? 10 : 'auto', // Bring dragged item to front
    };

    // --- Edit Handling ---
    const handleEditClick = () => {
        setEditedContent(block.content); // Reset edit buffer to current content
        setIsEditing(true);
    };

    const handleSaveEdit = () => {
        if (editedContent.trim() !== block.content) {
            onUpdateBlockContent(id, editedContent.trim());
        }
        setIsEditing(false);
    };

    const handleCancelEdit = () => {
        setEditedContent(block.content); // Revert changes
        setIsEditing(false);
    };

    const handleContentChange = (e) => {
        setEditedContent(e.target.value);
        // Auto-resize textarea
        if (textareaRef.current) {
            textareaRef.current.style.height = 'auto'; // Reset height
            textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`;
        }
    };

     // Auto-focus and resize textarea when editing starts
    useEffect(() => {
        if (isEditing && textareaRef.current) {
            textareaRef.current.focus();
            textareaRef.current.select(); // Select text for easy replacement
             // Adjust height initially and in case content was loaded
            textareaRef.current.style.height = 'auto';
            textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`;
        }
    }, [isEditing]);


    // Determine block type for styling/iconography
    const getBlockIcon = () => {
        switch(block.type) {
            case BlockTypes.MATERIAL_SUMMARY: return <span title="Œ†ŒµœÅŒØŒªŒ∑œàŒ∑ Œ•ŒªŒπŒ∫Œøœç">üìÑ</span>;
            case BlockTypes.CUSTOM_TEXT: return <span title="Œ†œÅŒøœÉŒ±œÅŒºŒøœÉŒºŒ≠ŒΩŒø ŒöŒµŒØŒºŒµŒΩŒø">‚úèÔ∏è</span>;
            case BlockTypes.PALETTE_ITEM: // Should be CANVAS_BLOCK on canvas
            case BlockTypes.CANVAS_BLOCK:
                 // Heuristic for icons based on title/content
                if (block.title?.includes('Œ°œåŒªŒøœÇ') || block.title?.includes('Œ†œÅŒøœÉœâœÄŒπŒ∫œåœÑŒ∑œÑŒ±')) return <span title={block.title}>üéØ</span>;
                if (block.title?.includes('ŒéœÜŒøœÇ')) return <span title={block.title}>üó£Ô∏è</span>;
                if (block.title?.includes('Œ†ŒªŒ±ŒØœÉŒπŒø') || block.title?.includes('ŒìŒΩœéœÉŒ∑')) return <span title={block.title}>üìö</span>;
                if (block.title?.includes('ŒöŒ±Œ∏ŒøŒ¥ŒÆŒ≥Œ∑œÉŒ∑') || block.title?.includes('ŒíŒÆŒºŒ±')) return <span title={block.title}>‚ùì</span>;
                if (block.title?.includes('Œ†Œ±œÅŒ¨Œ¥ŒµŒπŒ≥ŒºŒ±')) return <span title={block.title}>‚úÖ</span>;
                if (block.title?.includes('ŒëœÉœÜŒ¨ŒªŒµŒπŒ±')) return <span title={block.title}>üõ°Ô∏è</span>;
                return <span title="ŒüŒ¥Œ∑Œ≥ŒØŒ±">‚öôÔ∏è</span>; // Default instruction icon
            default: return <span title="ŒúœÄŒªŒøŒ∫">üß±</span>;
        }
    };

    return (
        <div ref={setNodeRef} style={style} className={`canvas-block ${currentlyDragging ? 'dragging' : ''} type-${block.type || 'default'}`}>
             {/* Drag Handle (only visible when not editing) */}
            {!isEditing && (
                <button {...attributes} {...listeners} className="drag-handle subtle-button" title="Œ£œçœÅŒµœÑŒµ Œ≥ŒπŒ± Œ±ŒΩŒ±Œ¥ŒπŒ¨œÑŒ±ŒæŒ∑">
                    <FaGripVertical />
                </button>
            )}

            <div className="block-content-area">
                 <div className="block-header">
                    {getBlockIcon()}
                    <strong>{block.title || 'ŒúœÄŒªŒøŒ∫ ŒüŒ¥Œ∑Œ≥ŒØŒ±œÇ'}</strong>
                 </div>
                {isEditing ? (
                    <textarea
                        ref={textareaRef}
                        value={editedContent}
                        onChange={handleContentChange}
                        onBlur={handleSaveEdit} // Save when focus is lost
                        rows={3} // Start with a few rows
                        className="block-textarea"
                    />
                ) : (
                    <p className="block-text" onDoubleClick={block.type === BlockTypes.CUSTOM_TEXT ? handleEditClick : undefined}>
                         {block.content}
                         {block.type === BlockTypes.CUSTOM_TEXT && <FaEdit className="edit-hint-icon" title="ŒîŒπœÄŒªœå Œ∫ŒªŒπŒ∫ Œ≥ŒπŒ± ŒµœÄŒµŒæŒµœÅŒ≥Œ±œÉŒØŒ±"/>}
                    </p>
                )}
            </div>

            {/* Action Buttons */}
            <div className="block-actions">
                {isEditing ? (
                    <>
                        <button onClick={handleSaveEdit} className="action-button save subtle-button" title="ŒëœÄŒøŒ∏ŒÆŒ∫ŒµœÖœÉŒ∑ Œ±ŒªŒªŒ±Œ≥œéŒΩ">
                            <FaCheck />
                        </button>
                        <button onClick={handleCancelEdit} className="action-button cancel subtle-button" title="ŒëŒ∫œçœÅœâœÉŒ∑ ŒµœÄŒµŒæŒµœÅŒ≥Œ±œÉŒØŒ±œÇ">
                            <FaTimes />
                        </button>
                    </>
                ) : (
                    <>
                     {/* Allow editing for custom text blocks */}
                     {block.type === BlockTypes.CUSTOM_TEXT && (
                        <button onClick={handleEditClick} className="action-button edit subtle-button" title="ŒïœÄŒµŒæŒµœÅŒ≥Œ±œÉŒØŒ± œÄŒµœÅŒπŒµœáŒøŒºŒ≠ŒΩŒøœÖ">
                             <FaEdit />
                         </button>
                     )}
                        <button onClick={() => onDeleteBlock(id)} className="action-button delete subtle-button" title="ŒîŒπŒ±Œ≥œÅŒ±œÜŒÆ ŒºœÄŒªŒøŒ∫">
                            <FaTrash />
                        </button>
                    </>
                )}
            </div>
        </div>
    );
}

export default SortableBlock;
