import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { registerUser } from '../../services/api';
import '../../styles/Auth.css'; // Reuse styles

function StudentRegister() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');

        if (password !== confirmPassword) {
            setError("Passwords do not match.");
            return;
        }
         if (password.length < 6) {
             setError("Password must be at least 6 characters long.");
            return;
        }

        setLoading(true);
        try {
            // Explicitly set role to 'student' (or rely on backend default)
            await registerUser(email, password, 'student');
            setSuccess('Επιτυχές εγγραφή μαθητή! Ας συνδεθούμε...');
            setTimeout(() => {
                navigate('/student/login'); // Redirect to student login
            }, 2000);
        } catch (err) {
            console.error("Student Registration error:", err);
            setError(err.response?.data?.error || 'Η εγγραφή απέτυχε! Δοκιμάστε ξανά.');
            setLoading(false);
        }
    };

    return (
        <div className="auth-container">
            <h2>Εγγραφή Μαθητή</h2>
            <form onSubmit={handleSubmit}>
                {error && <p className="error-message">{error}</p>}
                {success && <p className="success-message">{success}</p>}
                <div className="form-group">
                    <label htmlFor="email">Email:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                        disabled={loading}
                        autoComplete="username"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password (min 6 chars):</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        minLength="6"
                        disabled={loading}
                        autoComplete="new-password"
                    />
                </div>
                 <div className="form-group">
                    <label htmlFor="confirmPassword">Confirm Password:</label>
                    <input
                        type="password"
                        id="confirmPassword"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        required
                        minLength="6"
                        disabled={loading}
                        autoComplete="new-password"
                    />
                </div>
                <button type="submit" disabled={loading}>
                    {loading ? 'Registering...' : 'Register as Student'}
                </button>
            </form>
            <p className="auth-switch">
                Έχεις ήδη λογαριασμό μαθητή;? <Link to="/student/login">Συνδέσου ως μαθητής.</Link>
            </p>
             <p className="auth-switch">
                 Χρειάζεσαι λογαρισμό Καθηγητή; <Link to="/teacher/register">Κάνε εγγραφή ως Καθηγητής.</Link>
             </p>
        </div>
    );
}

export default StudentRegister;
