// src/components/common/RoleSelector.js
import React from 'react';
import { Link } from 'react-router-dom';
import '../../styles/Auth.css'; // Make sure path to CSS is correct

function RoleSelector() {
     return (
        <div className="role-selector-container">
            <h2>Welcome!</h2>
            <p>Are you a Teacher or a Student?</p>
            <div className="role-links">
                <Link to="/teacher/login" className="role-link-button">Teacher Login</Link>
                <Link to="/student/login" className="role-link-button">Student Login</Link>
            </div>
             <div className="register-links">
                <Link to="/teacher/register">Register as Teacher</Link> | <Link to="/student/register">Register as Student</Link>
            </div>
        </div>
    );
}

export default RoleSelector; // Don't forget to export!
