import React from 'react';
// Removed Link from here as it's not used directly in App.js
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import { AuthProvider, useAuth } from './contexts/AuthContext';

// --- Core Components ---
import RoleSelector from './components/common/RoleSelector';
import NotFound from './components/common/NotFound';

// --- Teacher Components ---
import TeacherDashboardLayout from './components/Dashboard/TeacherDashboard'; // Main Layout renamed
import TeacherOverview from './components/Dashboard/TeacherOverview';       // The initial card view
import PromptWorkshop from './components/Dashboard/PromptWorkshop';     // Moved functionality
import MaterialManagerPage from './components/Dashboard/MaterialManagerPage';// Separate materials view
import QuizListTeacher from './components/Quiz/Teacher/QuizListTeacher';
import QuizBuilder from './components/Quiz/Teacher/QuizBuilder';
import TeacherAnalyticsOverview from './components/Dashboard/TeacherAnalyticsOverview';

// --- Auth Components ---
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import StudentLogin from './components/Auth/StudentLogin';
import StudentRegister from './components/Auth/StudentRegister';

// --- Student Components ---
import StudentDashboard from './components/StudentDashboard/StudentDashboard';
import QuizTaker from './components/Quiz/Student/QuizTaker'; // <-- IMPORTED QUIZ TAKER
import QuizAnalytics from './components/Quiz/Teacher/QuizAnalytics';
import QuizResultStudent from './components/Quiz/Student/QuizResultStudent';

import './styles/App.css';

// --- Route Guards (Keep as before) ---
function ProtectedRoute({ children, allowedRoles }) {
    const { isAuthenticated, isLoading, user } = useAuth();
    const location = useLocation();
    if (isLoading) return <div className="loading-fullscreen">Checking Auth...</div>;
    if (!isAuthenticated) return <Navigate to="/login" state={{ from: location }} replace />;
    if (allowedRoles && !allowedRoles.includes(user?.role)) {
        console.warn(`Access denied: Role ${user?.role} for ${location.pathname}. Allowed: ${allowedRoles.join(', ')}`);
        const fallbackPath = user?.role === 'teacher' ? '/teacher/dashboard' : '/student/dashboard';
        return <Navigate to={fallbackPath} replace />;
    }
    return children;
}

function PublicRoute({ children }) {
    const { isAuthenticated, isLoading, user } = useAuth();
    if (isLoading) return <div className="loading-fullscreen">Loading...</div>;
    if (isAuthenticated) {
        const redirectPath = user?.role === 'teacher' ? '/teacher/dashboard' : '/student/dashboard';
        return <Navigate to={redirectPath} replace />;
    }
    return children;
}

// --- Main App Content with Routing ---
function AppContent() {
    const { isLoading } = useAuth();
    if (isLoading) return <div className="loading-fullscreen">Initializing...</div>;

    return (
        <Routes>
            {/* --- Public Routes --- */}
            <Route path="/" element={<PublicRoute><RoleSelector /></PublicRoute>} />
            <Route path="/teacher/login" element={<PublicRoute><Login /></PublicRoute>} />
            <Route path="/teacher/register" element={<PublicRoute><Register /></PublicRoute>} />
            <Route path="/student/login" element={<PublicRoute><StudentLogin /></PublicRoute>} />
            <Route path="/student/register" element={<PublicRoute><StudentRegister /></PublicRoute>} />
            <Route path="/login" element={<PublicRoute><RoleSelector /></PublicRoute>} />
            <Route path="/register" element={<PublicRoute><RoleSelector /></PublicRoute>} />

            {/* --- Protected Teacher Route with Nested Layout --- */}
            <Route
                path="/teacher/dashboard"
                element={<ProtectedRoute allowedRoles={['teacher']}><TeacherDashboardLayout /></ProtectedRoute>}
            >
                <Route index element={<TeacherOverview />} />
                <Route path="prompts" element={<PromptWorkshop />} />
                <Route path="quizzes" element={<QuizListTeacher />} />
                <Route path="quizzes/new" element={<QuizBuilder />} />
                <Route path="quizzes/edit/:quizId" element={<QuizBuilder />} />
				<Route path="quizzes/results/:quizId" element={<ProtectedRoute allowedRoles={['teacher']}><QuizAnalytics /></ProtectedRoute>} />
				<Route path="materials" element={<MaterialManagerPage />} />
				<Route path="analytics" element={<ProtectedRoute allowedRoles={['teacher']}><TeacherAnalyticsOverview /></ProtectedRoute>} />
                 <Route path="*" element={<Navigate to="/teacher/dashboard" replace />} /> {/* Fallback within dashboard */}
            </Route>

            {/* --- Protected Student Routes --- */}
            <Route
                path="/student/dashboard"
                element={<ProtectedRoute allowedRoles={['student', 'teacher']}><StudentDashboard /></ProtectedRoute>}
            />
            <Route
                path="/student/dashboard/quizzes/take/:quizId"
                element={<ProtectedRoute allowedRoles={['student', 'teacher']}><QuizTaker /></ProtectedRoute>}
            />
            {/* --- ADD NEW ROUTE FOR QUIZ RESULTS --- */}
            <Route
                path="/student/dashboard/attempts/:attemptId"
                element={<ProtectedRoute allowedRoles={['student', 'teacher']}><QuizResultStudent /></ProtectedRoute>}
            />
            {/* --- END ADD NEW ROUTE --- */}



            {/* --- Fallback Route (Top Level) --- */}
            <Route path="*" element={<NotFound />} />
        </Routes>
    );
}

// --- Root App Component ---
function App() {
    return (
        <Router>
            <AuthProvider>
                <div className="App">
                    <AppContent />
                </div>
            </AuthProvider>
        </Router>
    );
}

export default App;
