import React, { useState, useEffect } from 'react'; // Removed useCallback
import { Outlet, useLocation, useNavigate, Link } from 'react-router-dom'; // Add Link
import { useAuth } from '../../contexts/AuthContext';
import { FaSignOutAlt, FaComments, FaClipboardList, FaUser, FaGraduationCap, FaTachometerAlt } from 'react-icons/fa';
import AssistantViewStudent from './AssistantViewStudent';
import QuizListStudent from '../Quiz/Student/QuizListStudent';
// Import DiceBear
import { createAvatar } from '@dicebear/core';
import { adventurer } from '@dicebear/collection'; // Choose a style (e.g., adventurer, bottts, identicon)
import '../../styles/StudentDashboard.css';

function StudentDashboard() {
    const { user, logout } = useAuth();
    const [activeView, setActiveView] = useState('assistants');
    const [userAvatarSvg, setUserAvatarSvg] = useState('');

    // Generate user avatar once when user data is available
    useEffect(() => {
        if (user?.email) { // Use email or ID as seed for consistency
            const avatar = createAvatar(adventurer, {
                seed: user.email,
                size: 36, // Match size in CSS
                radius: 50, // Make it circle
                // Add other style options here if desired
            });
            const svgString = avatar.toString();
            setUserAvatarSvg(svgString);
        }
    }, [user]); // Re-generate if user changes

    const navigateToView = (view) => { setActiveView(view); };
	console.log("Current Active View:", activeView);
    return (
        <div className="student-dashboard-layout">
            {/* --- Header (No changes needed for structure) --- */}
            <header className="student-header">
                {/* ... h1 */}
                {/* ... header controls (keep user email, logout) */}
            </header>
             {/* --- NEW: Sidebar Navigation --- */}
             <div className="student-content-wrapper"> {/* Wrapper for sidebar + content */}
                 <aside className="student-sidebar">
                    <div className="sidebar-header">
                        <FaGraduationCap className="logo-icon" />
                        <h1>Student Portal</h1>
                    </div>
                     <nav className="student-main-nav">
                         <button
                             className={`student-nav-link ${activeView === 'assistants' ? 'active' : ''}`}
                             onClick={() => navigateToView('assistants')}
                         >
                             <FaComments /> AI Assistants
                         </button>
                         <button
                             className={`student-nav-link ${activeView === 'quizzes' ? 'active' : ''}`}
                             onClick={() => navigateToView('quizzes')}
                         >
                             <FaClipboardList /> Quizzes
                         </button>
                         {/* <button className={`student-nav-link ${activeView === 'results' ? 'active' : ''}`} onClick={() => navigateToView('results')}> <FaChartBar /> My Results </button> */}
                     </nav>
                     <div className="sidebar-footer">
                        {user && (
                            <div className="user-info">
                                <div className="avatar" dangerouslySetInnerHTML={{ __html: userAvatarSvg || '' }}>
                                    {/* SVG will be rendered here */}
                                </div>
                                <div className='user-details'>
                                     <span>{user.email}</span>
                                     <span title={user.id}>Student Account</span>
                                </div>

                            </div>
                         )}
                          <button onClick={logout} className="logout-button subtle-button" title="Logout">
                              <FaSignOutAlt /> Logout
                          </button>
                     </div>
                 </aside>

                 {/* --- Main Content Area --- */}
                 <main className="student-main-content-area">
                      {/* Content Display Area (Render based on state) */}
                      <div className="student-content-display">
                          {activeView === 'assistants' && <AssistantViewStudent />}
                          {activeView === 'quizzes' && <QuizListStudent />}
                          {/* Add other views later */}
                      </div>
                      {/* Or use Outlet if using nested routes */}
                      {/* <Outlet /> */}
                 </main>
             </div>
        </div>
    );
}

export default StudentDashboard;
