import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
// Προσθέτουμε τα εικονίδια που θα χρειαστούμε για το gamification
import { FaSignOutAlt, FaComments, FaClipboardList, FaGraduationCap, FaStar, FaBolt, FaFire } from 'react-icons/fa';
import AssistantViewStudent from './AssistantViewStudent';
import QuizListStudent from '../Quiz/Student/QuizListStudent';
import { createAvatar } from '@dicebear/core';
import { adventurer } from '@dicebear/collection';
import '../../styles/StudentDashboard.css';

function StudentDashboard() {
    const { user, logout } = useAuth();
    const [activeView, setActiveView] = useState('assistants');
    const [userAvatarSvg, setUserAvatarSvg] = useState('');

    // ΠΡΟΣΤΕΘΗΚΕ: State για τα δεδομένα παιχνιδοποίησης.
    // Στην πράξη, αυτά θα έρχονταν από τη βάση δεδομένων σου.
    const [gamificationData, setGamificationData] = useState({
        level: 8,
        progress: 75, // Πρόοδος σε ποσοστό για το επόμενο level
        xp: 2350,
        streak: 12,
    });

    useEffect(() => {
        if (user?.email) {
            // Αυξήσαμε λίγο το μέγεθος του avatar για καλύτερη εμφάνιση
            const avatar = createAvatar(adventurer, { seed: user.email, size: 40, radius: 50 });
            setUserAvatarSvg(avatar.toString());
        }
    }, [user]);

    const navigateToView = (view) => { setActiveView(view); };

    // Δυναμικός τίτλος για το header ανάλογα με το activeView
    const viewTitles = {
        assistants: 'Βοηθοί AI',
        quizzes: 'Τα Κουίζ μου',
    };

    return (
        <div className="student-dashboard-layout">
            {/* --- Sidebar: Το μοναδικό σημείο πλοήγησης --- */}
            <aside className="student-sidebar">
                <div className="sidebar-header">
                    <FaGraduationCap className="logo-icon" />
                    <h1>Πύλη Μαθητή</h1>
                </div>
                <nav className="student-main-nav">
                    <button
                        className={`student-nav-link ${activeView === 'assistants' ? 'active' : ''}`}
                        onClick={() => navigateToView('assistants')}
                    >
                        <FaComments /> <span>Βοηθοί AI</span>
                    </button>
                    <button
                        className={`student-nav-link ${activeView === 'quizzes' ? 'active' : ''}`}
                        onClick={() => navigateToView('quizzes')}
                    >
                        <FaClipboardList /> <span>Κουίζ</span>
                    </button>
                </nav>
                <div className="sidebar-footer">
                    {user && (
                        <div className="user-info">
                            <div className="avatar" dangerouslySetInnerHTML={{ __html: userAvatarSvg || '' }} />
                            <div className='user-details'>
                                <span>{user.email}</span>
                                <span title={user.id}>Λογαριασμός Μαθητή</span>
                            </div>
                        </div>
                    )}
                    <button onClick={logout} className="logout-button" title="Αποσύνδεση">
                        <FaSignOutAlt /> <span>Αποσύνδεση</span>
                    </button>
                </div>
            </aside>

            {/* --- Κύριο Περιεχόμενο --- */}
            <div className="student-main-content-wrapper">
                {/* --- Header: Τώρα είναι πιο απλό και δείχνει μόνο τον τίτλο της σελίδας --- */}
                <header className="student-header">
                    <h2>{viewTitles[activeView]}</h2>
                    {/* ΑΦΑΙΡΕΘΗΚΕ: Τα user controls μεταφέρθηκαν μόνιμα στο sidebar */}
                </header>

                <main className="student-main-content-area">
                    {/* ΠΡΟΣΤΕΘΗΚΕ: Η νέα, εμπλουτισμένη λωρίδα Gamification */}
                    <div className="gamify-strip">
                        <div className="gamify-card level">
                            <div className="ring" style={{ '--ring-progress': `${gamificationData.progress}%` }}>
                                <div className="ring-track"></div>
                                <div className="ring-center">
                                    <FaStar />
                                    <strong>{gamificationData.level}</strong>
                                </div>
                            </div>
                            <div className="meta">
                                <h4>Επίπεδο {gamificationData.level}</h4>
                                <p>{(4000 - gamificationData.xp).toLocaleString()} XP για το επόμενο</p>
                            </div>
                        </div>
                        <div className="gamify-card xp">
                           <FaBolt className="big-icon" />
                           <div className="meta">
                                <h4>{gamificationData.xp.toLocaleString()} XP</h4>
                                <p>Συνολικοί Πόντοι</p>
                           </div>
                        </div>
                        <div className="gamify-card streak">
                           <FaFire className="big-icon" />
                           <div className="meta">
                                <h4>{gamificationData.streak} Μέρες</h4>
                                <p>Συνεχόμενη Σειρά</p>
                           </div>
                        </div>
                    </div>

                    <div className="student-content-display">
                        {activeView === 'assistants' && <AssistantViewStudent />}
                        {activeView === 'quizzes' && <QuizListStudent />}
                    </div>
                </main>
            </div>
        </div>
    );
}

export default StudentDashboard;
