// frontend/src/components/Dashboard/PromptWorkshop.js
import React, { useState, useEffect, useCallback, useRef } from 'react';
import {
    DndContext, DragOverlay, closestCenter, PointerSensor, KeyboardSensor,
    useSensor, useSensors, MeasuringStrategy
} from '@dnd-kit/core';
import {
    SortableContext, arrayMove, verticalListSortingStrategy, sortableKeyboardCoordinates
} from '@dnd-kit/sortable';
import { v4 as uuidv4 } from 'uuid';

// Import Workshop Specific Components
import BlockPalette from './BlockPalette';
import PromptBuilder from './PromptBuilder/PromptBuilder';
import SortableBlock from './PromptBuilder/SortableBlock';
import Sandbox from './Sandbox';
import Modal from '../common/Modal';
import MaterialListForModal from './MaterialListForModal';
import PromptListForModal from './PromptListForModal';

// Import API Services
import { getMaterials, getTeacherPrompts, savePrompt, updatePrompt, getPromptDetails } from '../../services/api';

// Import Icons
import { FaSave, FaPlusCircle, FaEdit, FaInfoCircle, FaFileUpload, FaHistory, FaQuestion, FaSyncAlt, FaSpinner, FaGripVertical } from 'react-icons/fa'; // Added missing imports

// Import Styles
import '../../styles/PromptWorkshop.css';

// Block Types Definition
export const BlockTypes = {
    PALETTE_ITEM: 'palette-item',
    CANVAS_BLOCK: 'canvas-block',
    MATERIAL_SUMMARY: 'material-summary',
    CUSTOM_TEXT: 'custom-text',
};

// YOUR Categorized Pre-made Blocks - Assuming this structure exists or you'll provide your exact one
const initialPreMadeBlocks = {
    'ðŸŽ¯ Core & Persona': [
        { id: 'core-task-1', type: BlockTypes.PALETTE_ITEM, title: 'Core Task', content: 'Act as a helpful AI Teaching Assistant for [Grade Level] students focusing on [Subject/Topic].' },
        { id: 'persona-expert-1', type: BlockTypes.PALETTE_ITEM, title: 'Expert Persona', content: 'Adopt the persona of a knowledgeable and engaging expert in [Subject/Topic]. Explain concepts clearly.' },
        { id: 'persona-mentor-1', type: BlockTypes.PALETTE_ITEM, title: 'Mentor Persona', content: 'Adopt the persona of a supportive mentor, guiding students through their learning journey.' },
    ],
    'ðŸ—£ï¸ Tone & Style': [
        { id: 'tone-friendly-1', type: BlockTypes.PALETTE_ITEM, title: 'Friendly & Encouraging', content: 'Maintain a friendly, patient, and encouraging tone throughout the conversation. Use positive reinforcement.' },
        { id: 'tone-formal-1', type: BlockTypes.PALETTE_ITEM, title: 'Formal & Academic', content: 'Adopt a formal, objective, and academic tone suitable for serious study.' },
        { id: 'style-concise-1', type: BlockTypes.PALETTE_ITEM, title: 'Concise Answers', content: 'Keep your explanations clear, to the point, and as concise as possible without losing meaning.' },
        { id: 'style-detailed-1', type: BlockTypes.PALETTE_ITEM, title: 'Detailed Explanations', content: 'Provide detailed explanations, offering background, examples, and analogies where helpful.' },
    ],
    'ðŸ“š Context & Knowledge': [
        { id: 'ctx-material-1', type: BlockTypes.PALETTE_ITEM, title: 'Use Provided Material ONLY', content: 'Strictly base your answers and explanations on the lesson materials and context provided. If information is not present, state that clearly and do not invent answers.' },
        { id: 'ctx-summarize-material-1', type: BlockTypes.PALETTE_ITEM, title: 'Refer to Specific Material', content: 'When answering, explicitly refer to specific sections or ideas from [Name of Material Block] if relevant.' },
    ],
    'ðŸ’¡ Teaching Strategies': [
        { id: 'strat-socratic-1', type: BlockTypes.PALETTE_ITEM, title: 'Socratic Questioning', content: 'Instead of giving direct answers, guide students by asking a series of probing questions to help them arrive at the solution themselves.' },
        { id: 'strat-stepbystep-1', type: BlockTypes.PALETTE_ITEM, title: 'Step-by-Step Breakdown', content: 'For complex problems or processes, break them down into smaller, logical, and easy-to-follow steps. Explain each step clearly.' },
        { id: 'strat-analogy-1', type: BlockTypes.PALETTE_ITEM, title: 'Use Analogies', content: 'Use relevant analogies and real-world examples to make abstract concepts more understandable.' },
    ],
    'âœ… Examples & Scaffolding': [
        { id: 'ex-positive-1', type: BlockTypes.PALETTE_ITEM, title: 'Positive Example Q&A', content: 'Example Student Question: [Simple Question]\nExample Good AI Answer: [Clear, helpful, guiding answer based on your persona/tone]' },
        { id: 'scaffold-hints-1', type: BlockTypes.PALETTE_ITEM, title: 'Provide Hints', content: 'If a student is stuck, offer a small hint or a reminder of a relevant concept before providing more direct help.' },
    ],
    'ðŸ›¡ï¸ Safety & Ethics': [
        { id: 'safe-offtopic-1', type: BlockTypes.PALETTE_ITEM, title: 'Decline Off-Topic/Inappropriate', content: 'Politely refuse to answer questions that are unrelated to the subject, inappropriate, harmful, unethical, or request personal information.' },
        { id: 'safe-no-pii-1', type: BlockTypes.PALETTE_ITEM, title: 'No Personal Information', content: 'Do not ask for, request, store, or share any personally identifiable information (PII) from or about the student or others.' },
    ]
};


// Guided Tour Steps (Keep your existing steps here or adjust)
const guidedSteps = [
    { selector: '.workshop-column-palette .block-palette', content: '1. Block Library: Select a category, then click a block to add it to the canvas on the right.' },
    { selector: '.prompt-canvas-section', content: '2. Prompt Canvas: Build the AI\'s instructions here. Drag blocks to reorder, edit their content (double-click custom blocks), or delete them.' },
    { selector: '.prompt-metadata-controls', content: '3. Details & Actions: Name your AI Assistant, add a description for students, and set its visibility. Use the buttons to Save, create a New assistant, add Material Context, or Load a saved assistant.' },
    { selector: '.workshop-sandbox-area .sandbox', content: '4. Test Sandbox: Test your AI assistant in real-time! Type a student\'s question to see how the AI responds based on the prompt you\'ve built.' }
];


function PromptWorkshop() {
    const [materials, setMaterials] = useState([]);
    const [savedPrompts, setSavedPrompts] = useState([]);
    const [currentPrompt, setCurrentPrompt] = useState({ id: null, name: '', description: '', is_public: false, structure: [], system_prompt: '' });
    const [activeId, setActiveId] = useState(null);
    const [loadingState, setLoadingState] = useState({ materials: true, prompts: true, promptDetails: false });
    const [isSaving, setIsSaving] = useState(false);
    const [messages, setMessages] = useState({ error: '', success: '' });
    const [showLoadPromptModal, setShowLoadPromptModal] = useState(false);
    const [showAddMaterialModal, setShowAddMaterialModal] = useState(false);
    const [isTutorialActive, setIsTutorialActive] = useState(false);
    const [tutorialStep, setTutorialStep] = useState(0);
    const highlightedElementRef = useRef(null);

    const sensors = useSensors(
        useSensor(PointerSensor, { activationConstraint: { distance: 8 } }),
        useSensor(KeyboardSensor, { coordinateGetter: sortableKeyboardCoordinates })
    );

    const clearMessages = useCallback(() => setMessages({ error: '', success: '' }), []);
    const showSuccess = useCallback((msg, dur = 3500) => { setMessages({ error: '', success: msg }); setTimeout(clearMessages, dur); }, [clearMessages]);
    const showError = useCallback((msg) => { setMessages({ error: msg, success: '' }); }, []);

    const fetchAllMaterials = useCallback(async () => { setLoadingState(s => ({ ...s, materials: true })); try { const r = await getMaterials(); setMaterials(r.data || []); } catch (e) { console.error("Error fetching materials:", e); showError("Could not load materials."); setMaterials([]); } finally { setLoadingState(s => ({ ...s, materials: false })); } }, [showError]);
    const fetchAllPrompts = useCallback(async () => { setLoadingState(s => ({ ...s, prompts: true })); try { const r = await getTeacherPrompts(); setSavedPrompts(r.data || []); } catch (e) { console.error("Error fetching prompts:", e); showError("Could not load saved prompts."); setSavedPrompts([]); } finally { setLoadingState(s => ({ ...s, prompts: false })); } }, [showError]);
    useEffect(() => { fetchAllMaterials(); fetchAllPrompts(); }, [fetchAllMaterials, fetchAllPrompts]);

    const handleAddBlock = useCallback((blockFromPalette) => {
        clearMessages();
        const newBlock = {
            id: `canvas-${uuidv4()}`,
            type: BlockTypes.CANVAS_BLOCK,
            title: blockFromPalette.title,
            content: blockFromPalette.content,
        };
        console.log("PromptWorkshop: Adding new block:", newBlock);
        setCurrentPrompt(p => ({ ...p, structure: [...p.structure, newBlock] }));
    }, [clearMessages]);

    const handleAddMaterialSummary = useCallback((material) => {
        clearMessages();
        if (!material?.id) { showError("Invalid material selected."); return; }
        const summaryBlock = { id: `mat-${material.id}-${uuidv4()}`, type: BlockTypes.MATERIAL_SUMMARY, title: `ðŸ“„ Context from: ${material.name}`, content: `[USE_FULL_TEXT_FROM_MATERIAL_ID:${material.id}] Information from material titled "${material.name}".`, isMaterialBlock: true, materialId: material.id, materialName: material.name };
        setCurrentPrompt(prev => ({ ...prev, structure: [...prev.structure, summaryBlock] }));
        showSuccess(`Context from "${material.name}" added (AI will use full text).`);
        setShowAddMaterialModal(false);
    }, [clearMessages, showError, showSuccess]);

    const handleAddCustomTextBlock = useCallback(() => {
        clearMessages();
        const nb = { id: `custom-${uuidv4()}`, type: BlockTypes.CUSTOM_TEXT, title: 'âœï¸ Custom Instruction', content: 'Enter your custom instruction...' };
        setCurrentPrompt(p => ({ ...p, structure: [...p.structure, nb] }));
    }, [clearMessages]);

    const handleDeleteBlock = useCallback((id) => { clearMessages(); setCurrentPrompt(p => ({ ...p, structure: p.structure.filter(b => b.id !== id) })); }, [clearMessages]);
    const handleUpdateBlockContent = useCallback((id, content) => { clearMessages(); setCurrentPrompt(p => ({ ...p, structure: p.structure.map(b => b.id === id ? { ...b, content } : b) })); }, [clearMessages]);
    const handlePromptNameChange = (e) => setCurrentPrompt(p => ({ ...p, name: e.target.value }));
    const handlePromptDescriptionChange = (e) => setCurrentPrompt(p => ({ ...p, description: e.target.value }));
    const handlePromptPublicToggle = (e) => setCurrentPrompt(p => ({ ...p, is_public: e.target.checked }));

    const handleSavePrompt = useCallback(async () => {
        clearMessages();
        if (!currentPrompt.name.trim()) { showError("AI Assistant Name is required."); return; }
        // No need to check for empty structure if backend handles it gracefully
        setIsSaving(true);
        const payload = { name: currentPrompt.name.trim(), description: currentPrompt.description.trim(), is_public: currentPrompt.is_public, structure: currentPrompt.structure };
        try {
            let response; let msg = '';
            if (currentPrompt.id) {
                response = await updatePrompt(currentPrompt.id, payload);
                msg = `Assistant "${response.data.name}" updated successfully.`;
                setSavedPrompts(prev => prev.map(p => p.id === response.data.id ? { ...p, ...response.data } : p));
                // The response.data for PUT /prompts only contains metadata, not full structure/system_prompt.
                // We trust the payload.structure for visual consistency unless fetching details again.
                // Backend should have rebuilt and saved the system_prompt.
                // Fetch details again to get the updated system_prompt for the sandbox accurately.
                const updatedDetails = await getPromptDetails(currentPrompt.id);
                setCurrentPrompt(prev => ({ ...prev, ...updatedDetails.data }));
            } else {
                response = await savePrompt(payload);
                msg = `Assistant "${response.data.name}" created successfully.`;
                 // Response for new prompt also includes the backend-generated system_prompt.
                setCurrentPrompt(prev => ({ ...prev, id: response.data.id, ...response.data, system_prompt: response.data.system_prompt }));
                setSavedPrompts(prev => [response.data, ...prev]);
            }
            showSuccess(msg);
        } catch (err) {
            console.error("Error saving prompt:", err);
            showError(err.response?.data?.error || "Failed to save AI assistant.");
        } finally { setIsSaving(false); }
    }, [currentPrompt, showError, showSuccess, clearMessages]);

    const handleLoadPrompt = useCallback(async (promptToLoad) => {
        clearMessages();
        if (!promptToLoad?.id) { showError("Invalid assistant selected."); setShowLoadPromptModal(false); return; }
        setLoadingState(s => ({ ...s, promptDetails: true }));
        try {
            const response = await getPromptDetails(promptToLoad.id);
            const fp = response.data;
            setCurrentPrompt({
                id: fp.id, name: fp.name || '', description: fp.description || '',
                is_public: !!fp.is_public,
                structure: Array.isArray(fp.structure) ? fp.structure : [],
                system_prompt: fp.system_prompt || '' // This comes from backend
            });
            showSuccess(`Loaded assistant: "${fp.name}"`);
        } catch (err) { console.error("Error loading prompt details:", err); showError(`Failed to load "${promptToLoad.name}".`);
        } finally { setLoadingState(s => ({ ...s, promptDetails: false })); setShowLoadPromptModal(false); }
    }, [showError, showSuccess, clearMessages]);

    const handleNewPrompt = useCallback(() => { clearMessages(); setCurrentPrompt({ id: null, name: '', description: '', is_public: false, structure: [], system_prompt: '' }); showSuccess("New assistant canvas is ready."); }, [clearMessages, showSuccess]);

    const handleDragStart = (event) => { clearMessages(); setActiveId(event.active.id); };
    const handleDragEnd = (event) => { const { active, over } = event; setActiveId(null); if (over && active.id !== over.id) { const oldIdx = currentPrompt.structure.findIndex(i => i.id === active.id); const newIdx = currentPrompt.structure.findIndex(i => i.id === over.id); if (oldIdx !== -1 && newIdx !== -1) { setCurrentPrompt(p => ({ ...p, structure: arrayMove(p.structure, oldIdx, newIdx) })); } } };
    const draggedBlockData = activeId ? currentPrompt.structure.find(b => b.id === activeId) : null;
    // Sandbox needs either the locally constructed prompt from current blocks, or if a prompt is loaded, its specific system_prompt
    const systemPromptForSandbox = currentPrompt.system_prompt ? currentPrompt.system_prompt : currentPrompt.structure.map(block => block.content).join("\n\n");


    const startTutorial = () => { setIsTutorialActive(true); setTutorialStep(0); };
    const nextTutorialStep = () => { if (tutorialStep < guidedSteps.length - 1) setTutorialStep(s => s + 1); else setIsTutorialActive(false); };
    const endTutorial = () => {
        if (highlightedElementRef.current) {
            highlightedElementRef.current.classList.remove('tutorial-highlight');
            highlightedElementRef.current = null;
        }
        setIsTutorialActive(false);
    };

    useEffect(() => {
        if (highlightedElementRef.current) { highlightedElementRef.current.classList.remove('tutorial-highlight'); highlightedElementRef.current = null; }
        if (isTutorialActive && guidedSteps[tutorialStep]) {
            const selector = guidedSteps[tutorialStep].selector;
            try { const element = document.querySelector(selector); if (element) { element.scrollIntoView({ behavior: 'smooth', block: 'center', inline: 'nearest' }); element.classList.add('tutorial-highlight'); highlightedElementRef.current = element; } else { console.warn(`Tutorial: Element "${selector}" not found for step ${tutorialStep}.`); }
            } catch (e) { console.warn(`Tutorial: Invalid selector "${selector}" for step ${tutorialStep}.`); }
        }
        return () => { if (highlightedElementRef.current) { highlightedElementRef.current.classList.remove('tutorial-highlight'); } };
    }, [isTutorialActive, tutorialStep]);

    return (
        <div className="prompt-workshop-page page-content-wrapper">
            <div className="page-header">
                <h2><FaEdit /> AI Assistant Workshop</h2>
                <button onClick={startTutorial} className="button subtle-button">
                    <FaQuestion /> Quick Tour
                </button>
            </div>

            {(messages.error || messages.success) &&
                <div className={`message global-message ${messages.error ? 'error-message' : 'success-message'}`}>
                    {messages.error || messages.success}
                    <button onClick={clearMessages} title="Dismiss">X</button>
                </div>
            }

            <DndContext sensors={sensors} collisionDetection={closestCenter} onDragStart={handleDragStart} onDragEnd={handleDragEnd} measuring={{ droppable: { strategy: MeasuringStrategy.Always } }}>
                <div className="prompt-workshop-container">
                    <div className="workshop-main-area">
                        <aside className="workshop-column workshop-column-palette">
                            <BlockPalette
                                blocksByCategory={initialPreMadeBlocks}
                                onAddBlock={handleAddBlock}
                                onAddCustom={handleAddCustomTextBlock}
                            />
                        </aside>
                        <main className="workshop-column workshop-column-canvas">
                            <div className="prompt-canvas-section widget">
                                <div className="prompt-metadata-controls">
                                    <div className="prompt-meta-row-1">
                                        <input type="text" placeholder="AI Assistant Name (Required)" value={currentPrompt.name} onChange={handlePromptNameChange} className="prompt-name-input" required />
                                        <div className="prompt-actions-main">
                                            <button className="button primary-button" onClick={handleSavePrompt} disabled={isSaving || !currentPrompt.name.trim()}> <FaSave /> {isSaving ? 'Saving...' : (currentPrompt.id ? 'Update' : 'Save New')} </button>
                                            <button onClick={handleNewPrompt} className="button subtle-button" title="Clear Canvas & Start New"><FaSyncAlt /> New </button>
                                        </div>
                                    </div>
                                    <div className="prompt-meta-row-2">
                                        <textarea placeholder="Short description for students (optional)..." value={currentPrompt.description} onChange={handlePromptDescriptionChange} className="prompt-description-input" rows={2} />
                                    </div>
                                    <div className="prompt-actions-extra">
                                        <div className="prompt-load-add-buttons">
                                            <button className="button subtle-button" onClick={() => setShowAddMaterialModal(true)} title="Add context from your uploaded material"><FaFileUpload /> Add Material Context</button>
                                            <button className="button subtle-button" onClick={() => setShowLoadPromptModal(true)} title="Load a previously saved assistant"><FaHistory /> Load Assistant</button>
                                        </div>
                                        <div className="prompt-public-toggle">
                                            <input type="checkbox" id={`is_public_toggle_PW_${currentPrompt.id || 'newP'}`} checked={currentPrompt.is_public} onChange={handlePromptPublicToggle} disabled={isSaving} />
                                            <label htmlFor={`is_public_toggle_PW_${currentPrompt.id || 'newP'}`} className={currentPrompt.is_public ? 'is-public' : ''} title="Make this available to students?">
                                                {currentPrompt.is_public ? 'Public (Students can use)' : 'Private (Teachers only)'}
                                            </label>
                                        </div>
                                    </div>
                                </div>
                                <SortableContext items={currentPrompt.structure.map(b => b.id)} strategy={verticalListSortingStrategy}>
                                    <PromptBuilder blocks={currentPrompt.structure} onDeleteBlock={handleDeleteBlock} onUpdateBlockContent={handleUpdateBlockContent} activeId={activeId} />
                                </SortableContext>
                            </div>
                        </main>
                    </div>

                    <div className="workshop-sandbox-area">
                        <Sandbox
                            currentPromptStructure={currentPrompt.structure}
                            systemPromptForSandbox={systemPromptForSandbox} // Pass the determined system prompt
                            promptIdToUseIfNoStructure={currentPrompt.id}
                            showError={showError}
                            showSuccess={showSuccess}
                            clearMessages={clearMessages}
                        />
                    </div>
                </div>

                <DragOverlay dropAnimation={null}>
                    {activeId && draggedBlockData ? (<SortableBlock id={draggedBlockData.id} block={draggedBlockData} isDraggingOverlay={true} onDeleteBlock={()=>{}} onUpdateBlockContent={()=>{}} />) : null}
                </DragOverlay>
            </DndContext>

            {showLoadPromptModal && (
                <Modal title="Load Saved AI Assistant" onClose={() => setShowLoadPromptModal(false)} width="700px" showFooter={true}>
                    {loadingState.promptDetails ? <div className="loading-items modal-loading"><FaSpinner className="spin" /> Fetching details...</div>
                        : <PromptListForModal prompts={savedPrompts} onLoadPrompt={handleLoadPrompt} isLoading={loadingState.prompts} currentPromptId={currentPrompt.id} showError={showError} />
                    }
                </Modal>
            )}
            {showAddMaterialModal && (
                <Modal title="Add Material Context to Prompt" onClose={() => setShowAddMaterialModal(false)} width="700px" showFooter={true}>
                    <MaterialListForModal materials={materials} onMaterialClick={handleAddMaterialSummary} isLoading={loadingState.materials} showError={showError} />
                </Modal>
            )}

            {isTutorialActive && guidedSteps[tutorialStep] && (
                <div className="tutorial-overlay-backdrop" onClick={endTutorial}>
                    <div className="tutorial-tooltip-wrapper" style={{ /* Consider JS positioning */ }}>
                        <div className="tutorial-tooltip" onClick={e => e.stopPropagation()}>
                            <p>{guidedSteps[tutorialStep].content}</p>
                            <div style={{ marginTop: '1em', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                <div>
                                    {tutorialStep > 0 && <button onClick={() => setTutorialStep(s => s-1)} className='button subtle-button small'>Previous</button>}
                                </div>
                                <span>Step {tutorialStep + 1} of {guidedSteps.length}</span>
                                <div>
                                    <button onClick={nextTutorialStep} className='button primary-button small'>
                                        {tutorialStep < guidedSteps.length - 1 ? 'Next' : 'Finish'}
                                    </button>
                                    {tutorialStep < guidedSteps.length - 1 && <button onClick={endTutorial} className='button subtle-button small close-tutorial'>Skip</button> }
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default PromptWorkshop;
