import React from 'react';
import SortableBlock from './SortableBlock'; // We need the individual sortable item
import { FaPlus } from 'react-icons/fa'; // Use an icon for the empty state
// CSS is handled by TeacherDashboard.css and PromptBuilder.css

function PromptBuilder({ blocks, onDeleteBlock, onUpdateBlockContent, activeId }) {
    return (
        <div className="prompt-builder-canvas">
            {blocks && blocks.length > 0 ? (
                blocks.map((block) => (
                    <SortableBlock
                        key={block.id}
                        id={block.id} // Required by useSortable
                        block={block}
                        onDeleteBlock={onDeleteBlock}
                        onUpdateBlockContent={onUpdateBlockContent}
                        isOpacityReduced={activeId === block.id} // Example: Reduce opacity while dragging original
                    />
                ))
            ) : (
                <div className="empty-canvas-message">
                     <FaPlus />
                    <p>Drag blocks here or click items from the left panel to start building your prompt!</p>
                </div>
            )}
        </div>
    );
}

export default PromptBuilder;
