import React, { useState } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { loginUser } from '../../services/api';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import '../../styles/Auth.css'; // Reuse styles

function StudentLogin() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  // Default redirect for students
  const from = location.state?.from?.pathname || "/student/dashboard";

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    try {
      const response = await loginUser(email, password);
      await login(response.data.access_token);
       // Navigate after login state is hopefully updated
       navigate(from, { replace: true });
    } catch (err) {
      console.error("Student Login error:", err);
      setError(err.response?.data?.error || 'Αποτυχές σύνδεση! Ελέγξτε τα στοιχεία εισόδου.');
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <h2>Student Login</h2>
      <form onSubmit={handleSubmit}>
        {error && <p className="error-message">{error}</p>}
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            disabled={loading}
            autoComplete="username"
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            disabled={loading}
            autoComplete="current-password"
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Logging in...' : 'Login as Student'}
        </button>
      </form>
       <p className="auth-switch">
         Need an account? <Link to="/student/register">Register as Student</Link>
       </p>
        <p className="auth-switch">
         Are you a Teacher? <Link to="/teacher/login">Teacher Login</Link>
       </p>
    </div>
  );
}

export default StudentLogin;
