import React, { useState, useRef, useEffect } from 'react';
import { askAssistant } from '../../services/api';
import { FaPaperPlane, FaUserGraduate, FaRobot, FaSpinner, FaArrowLeft } from 'react-icons/fa';
// Import DiceBear directly here too (or pass user avatar from parent)
import { createAvatar } from '@dicebear/core';
import { adventurer, bottts } from '@dicebear/collection'; // Assuming 'adventurer' for user
import { useAuth } from '../../contexts/AuthContext'; // To get user info for avatar seed
import '../../styles/StudentDashboard.css';

// User Avatar Component (using DiceBear)
const UserAvatar = () => {
    const { user } = useAuth();
    const [svg, setSvg] = useState('');

    useEffect(() => {
         if (user?.email) {
             const avatar = createAvatar(adventurer, { seed: user.email, size: 32, radius: 50 });
             setSvg(avatar.toString());
         }
     }, [user]);

    return (
         <div className="message-avatar-container user-avatar">
              <div className="avatar" dangerouslySetInnerHTML={{ __html: svg }} title="You" />
         </div>
    );
};

// Assistant Avatar Component (using passed SVG)
const AssistantAvatar = ({ svgString }) => (
    <div className="message-avatar-container assistant-avatar">
         <div className="avatar" dangerouslySetInnerHTML={{ __html: svgString }} title="AI Assistant" />
     </div>
);


function ChatInterface({ promptId, promptName, promptDescription, assistantAvatarSvg, onBack }) {
    const [messages, setMessages] = useState([]);
    const [inputMessage, setInputMessage] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');
    const chatEndRef = useRef(null);

    useEffect(() => { chatEndRef.current?.scrollIntoView({ behavior: 'smooth' }); }, [messages]);
    useEffect(() => { setMessages([]); setError(''); setInputMessage(''); setIsLoading(false); }, [promptId]);


    const handleSendMessage = async (e) => { /* ... same as before ... */
         e.preventDefault(); const userMessageContent = inputMessage.trim(); if (!userMessageContent || isLoading) return; setError(''); const newUserMessage = { role: 'user', content: userMessageContent }; setMessages(prev => [...prev, newUserMessage]); setInputMessage(''); setIsLoading(true); try { const response = await askAssistant(promptId, userMessageContent); const assistantMessage = { role: 'assistant', content: response.data.response }; setMessages(prev => [...prev, assistantMessage]); } catch (err) { console.error("Error asking assistant:", err); const errorMessage = err.response?.data?.error || "Sorry, I couldn't get a response."; setError(errorMessage); setMessages(prev => [...prev, { role: 'assistant', content: `Error: ${errorMessage}` }]); } finally { setIsLoading(false); }
     };

    // Generate assistant avatar placeholder if not passed (optional)
    const defaultAssistantAvatar = assistantAvatarSvg || createAvatar(bottts, { seed: promptId, size: 40 }).toString();


    return (
        <div className="chat-interface-container widget">
             <div className="chat-header">
                {onBack && ( <button onClick={onBack} className='subtle-button back-button' title="Back to list"> <FaArrowLeft /> </button> )}
                <div className='avatar' dangerouslySetInnerHTML={{ __html: defaultAssistantAvatar }}/>
                 <div className="chat-header-info">
                    <h3>{promptName || 'AI Assistant'}</h3>
                    <p>{promptDescription || 'Ready to help!'}</p>
                </div>
             </div>
            <div className="chat-messages">
                {messages.map((msg, index) => (
                    <div key={index} className={`message-bubble ${msg.role}`}>
                         {/* Render Avatars */}
                         {msg.role === 'user' ? <UserAvatar /> : <AssistantAvatar svgString={defaultAssistantAvatar} />}
                        <div className="message-content">
                            <pre>{msg.content}</pre>
                        </div>
                    </div>
                ))}
                {isLoading && (
                    <div className="message-bubble assistant loading">
                         <AssistantAvatar svgString={defaultAssistantAvatar}/> {/* Use avatar here too */}
                         <div className="message-content">Thinking... <FaSpinner className='spin'/></div>
                    </div>
                 )}
                <div ref={chatEndRef} />
            </div>

             {error && <p className="error-message chat-error">{error}</p>}

            <form className="chat-input-form" onSubmit={handleSendMessage}>
                <input type="text" value={inputMessage} onChange={(e) => setInputMessage(e.target.value)} placeholder="Ask your question..." disabled={isLoading} aria-label="Chat input" />
                <button type="submit" disabled={isLoading || !inputMessage.trim()} title="Send Message"> {isLoading ? <FaSpinner className="spin" /> : <FaPaperPlane />} </button>
            </form>
        </div>
    );
}

export default ChatInterface;
