const guidedSteps = [
    { selector: '.workshop-column-palette .block-palette', content: '1. Î’Î¹Î²Î»Î¹Î¿Î¸Î®ÎºÎ· ÎœÏ€Î»Î¿Îº: Î•Ï€Î¹Î»Î­Î¾Ï„Îµ Î¼Î¹Î± ÎºÎ±Ï„Î·Î³Î¿ÏÎ¯Î± ÎºÎ±Î¹ Î¼ÎµÏ„Î¬ ÎºÎ¬Î½Ï„Îµ ÎºÎ»Î¹Îº ÏƒÎµ Î­Î½Î± Î¼Ï€Î»Î¿Îº Î³Î¹Î± Î½Î± Ï„Î¿ Ï€ÏÎ¿ÏƒÎ¸Î­ÏƒÎµÏ„Îµ ÏƒÏ„Î¿Î½ ÎºÎ±Î¼Î²Î¬ Î´ÎµÎ¾Î¹Î¬.' },
    { selector: '.prompt-canvas-section', content: '2. ÎšÎ±Î¼Î²Î¬Ï‚ ÎŸÎ´Î·Î³Î¹ÏŽÎ½: Î”Î·Î¼Î¹Î¿Ï…ÏÎ³Î®ÏƒÏ„Îµ Ï„Î¹Ï‚ Î¿Î´Î·Î³Î¯ÎµÏ‚ Ï„Î¿Ï… Î²Î¿Î·Î¸Î¿Ï ÎµÎ´ÏŽ. Î£ÏÏÎµÏ„Îµ Ï„Î± Î¼Ï€Î»Î¿Îº Î³Î¹Î± Î½Î± Î±Î»Î»Î¬Î¾ÎµÏ„Îµ Ï„Î· ÏƒÎµÎ¹ÏÎ¬, ÎµÏ€ÎµÎ¾ÎµÏÎ³Î±ÏƒÏ„ÎµÎ¯Ï„Îµ Ï„Î¿ Ï€ÎµÏÎ¹ÎµÏ‡ÏŒÎ¼ÎµÎ½ÏŒ Ï„Î¿Ï…Ï‚ (Î´Î¹Ï€Î»ÏŒ ÎºÎ»Î¹Îº ÏƒÏ„Î± Ï€ÏÎ¿ÏƒÎ±ÏÎ¼Î¿ÏƒÎ¼Î­Î½Î±) Î® Î´Î¹Î±Î³ÏÎ¬ÏˆÏ„Îµ Ï„Î±.' },
    { selector: '.prompt-metadata-controls', content: '3. Î›ÎµÏ€Ï„Î¿Î¼Î­ÏÎµÎ¹ÎµÏ‚ & Î•Î½Î­ÏÎ³ÎµÎ¹ÎµÏ‚: Î”ÏŽÏƒÏ„Îµ ÏŒÎ½Î¿Î¼Î± ÏƒÏ„Î¿Î½ Î²Î¿Î·Î¸ÏŒ ÏƒÎ±Ï‚, Ï€ÏÎ¿ÏƒÎ¸Î­ÏƒÏ„Îµ Ï€ÎµÏÎ¹Î³ÏÎ±Ï†Î® Î³Î¹Î± Ï„Î¿Ï…Ï‚ Î¼Î±Î¸Î·Ï„Î­Ï‚ ÎºÎ±Î¹ Î¿ÏÎ¯ÏƒÏ„Îµ Ï„Î·Î½ Î¿ÏÎ±Ï„ÏŒÏ„Î·Ï„Î¬ Ï„Î¿Ï…. Î§ÏÎ·ÏƒÎ¹Î¼Î¿Ï€Î¿Î¹Î®ÏƒÏ„Îµ Ï„Î± ÎºÎ¿Ï…Î¼Ï€Î¹Î¬ Î³Î¹Î± Î‘Ï€Î¿Î¸Î®ÎºÎµÏ…ÏƒÎ·, Î´Î·Î¼Î¹Î¿Ï…ÏÎ³Î¯Î± ÎÎ­Î¿Ï…, Ï€ÏÎ¿ÏƒÎ¸Î®ÎºÎ· Î¥Î»Î¹ÎºÎ¿Ï Î® Î³Î¹Î± Î½Î± Î¦Î¿ÏÏ„ÏŽÏƒÎµÏ„Îµ Î­Î½Î±Î½ Î±Ï€Î¿Î¸Î·ÎºÎµÏ…Î¼Î­Î½Î¿ Î²Î¿Î·Î¸ÏŒ.' },
    { selector: '.workshop-sandbox-area .sandbox', content: '4. Î ÎµÏÎ¹Î¿Ï‡Î® Î”Î¿ÎºÎ¹Î¼ÏŽÎ½: Î”Î¿ÎºÎ¹Î¼Î¬ÏƒÏ„Îµ Ï„Î¿Î½ Î²Î¿Î·Î¸ÏŒ ÏƒÎ±Ï‚ ÏƒÎµ Ï€ÏÎ±Î³Î¼Î±Ï„Î¹ÎºÏŒ Ï‡ÏÏŒÎ½Î¿! Î“ÏÎ¬ÏˆÏ„Îµ Î¼Î¹Î± ÎµÏÏŽÏ„Î·ÏƒÎ· Î¼Î±Î¸Î·Ï„Î® Î³Î¹Î± Î½Î± Î´ÎµÎ¯Ï„Îµ Ï€ÏŽÏ‚ Î±Ï€Î±Î½Ï„Î¬ Î¼Îµ Î²Î¬ÏƒÎ· Ï„Î¹Ï‚ Î¿Î´Î·Î³Î¯ÎµÏ‚ Ï€Î¿Ï… Î´Î·Î¼Î¹Î¿Ï…ÏÎ³Î®ÏƒÎ±Ï„Îµ.' }
];

function PromptWorkshop() {
    const [materials, setMaterials] = useState([]);
    const [savedPrompts, setSavedPrompts] = useState([]);
    const [currentPrompt, setCurrentPrompt] = useState({ id: null, name: '', description: '', is_public: false, structure: [], system_prompt: '' });
    const [activeId, setActiveId] = useState(null);
    const [loadingState, setLoadingState] = useState({ materials: true, prompts: true, promptDetails: false });
    const [isSaving, setIsSaving] = useState(false);
    const [messages, setMessages] = useState({ error: '', success: '' });
    const [showLoadPromptModal, setShowLoadPromptModal] = useState(false);
    const [showAddMaterialModal, setShowAddMaterialModal] = useState(false);
    const [isTutorialActive, setIsTutorialActive] = useState(false);
    const [tutorialStep, setTutorialStep] = useState(0);
    const highlightedElementRef = useRef(null);

    const sensors = useSensors(
        useSensor(PointerSensor, { activationConstraint: { distance: 8 } }),
        useSensor(KeyboardSensor, { coordinateGetter: sortableKeyboardCoordinates })
    );

    const clearMessages = useCallback(() => setMessages({ error: '', success: '' }), []);
    const showSuccess = useCallback((msg, dur = 3500) => { setMessages({ error: '', success: msg }); setTimeout(clearMessages, dur); }, [clearMessages]);
    const showError = useCallback((msg) => { setMessages({ error: msg, success: '' }); }, []);

    const fetchAllMaterials = useCallback(async () => { setLoadingState(s => ({ ...s, materials: true })); try { const r = await getMaterials(); setMaterials(r.data || []); } catch (e) { console.error("Error fetching materials:", e); showError("Î‘Î´Ï…Î½Î±Î¼Î¯Î± Ï†ÏŒÏÏ„Ï‰ÏƒÎ·Ï‚ Ï…Î»Î¹ÎºÏŽÎ½."); setMaterials([]); } finally { setLoadingState(s => ({ ...s, materials: false })); } }, [showError]);
    const fetchAllPrompts = useCallback(async () => { setLoadingState(s => ({ ...s, prompts: true })); try { const r = await getTeacherPrompts(); setSavedPrompts(r.data || []); } catch (e) { console.error("Error fetching prompts:", e); showError("Î‘Î´Ï…Î½Î±Î¼Î¯Î± Ï†ÏŒÏÏ„Ï‰ÏƒÎ·Ï‚ Î±Ï€Î¿Î¸Î·ÎºÎµÏ…Î¼Î­Î½Ï‰Î½ Î²Î¿Î·Î¸ÏŽÎ½."); setSavedPrompts([]); } finally { setLoadingState(s => ({ ...s, prompts: false })); } }, [showError]);
    useEffect(() => { fetchAllMaterials(); fetchAllPrompts(); }, [fetchAllMaterials, fetchAllPrompts]);

    const handleAddBlock = useCallback((blockFromPalette) => {
        clearMessages();
        const newBlock = {
            id: `canvas-${uuidv4()}`,
            type: BlockTypes.CANVAS_BLOCK,
            title: blockFromPalette.title,
            content: blockFromPalette.content,
        };
        console.log("PromptWorkshop: Adding new block:", newBlock);
        setCurrentPrompt(p => ({ ...p, structure: [...p.structure, newBlock] }));
    }, [clearMessages]);

    const handleAddMaterialSummary = useCallback((material) => {
        clearMessages();
        if (!material?.id) { showError("Î•Ï€Î¹Î»Î­Ï‡Î¸Î·ÎºÎµ Î¼Î· Î­Î³ÎºÏ…ÏÎ¿ Ï…Î»Î¹ÎºÏŒ."); return; }
        const summaryBlock = { id: `mat-${material.id}-${uuidv4()}`, type: BlockTypes.MATERIAL_SUMMARY, title: `ðŸ“„ Î Î»Î±Î¯ÏƒÎ¹Î¿ Î±Ï€ÏŒ: ${material.name}`, content: `[USE_FULL_TEXT_FROM_MATERIAL_ID:${material.id}] Î Î»Î·ÏÎ¿Ï†Î¿ÏÎ¯ÎµÏ‚ Î±Ï€ÏŒ Ï„Î¿ Ï…Î»Î¹ÎºÏŒ Î¼Îµ Ï„Î¯Ï„Î»Î¿ "${material.name}".`, isMaterialBlock: true, materialId: material.id, materialName: material.name };
        setCurrentPrompt(prev => ({ ...prev, structure: [...prev.structure, summaryBlock] }));
        showSuccess(`Î ÏÎ¿ÏƒÏ„Î­Î¸Î·ÎºÎµ Ï„Î¿ Ï€Î»Î±Î¯ÏƒÎ¹Î¿ Î±Ï€ÏŒ "${material.name}" (Ï„Î¿ AI Î¸Î± Ï‡ÏÎ·ÏƒÎ¹Î¼Î¿Ï€Î¿Î¹Î®ÏƒÎµÎ¹ Ï„Î¿ Ï€Î»Î®ÏÎµÏ‚ ÎºÎµÎ¯Î¼ÎµÎ½Î¿).`);
        setShowAddMaterialModal(false);
    }, [clearMessages, showError, showSuccess]);

    const handleAddCustomTextBlock = useCallback(() => {
        clearMessages();
        const nb = { id: `custom-${uuidv4()}`, type: BlockTypes.CUSTOM_TEXT, title: 'âœï¸ Î ÏÎ¿ÏƒÎ±ÏÎ¼Î¿ÏƒÎ¼Î­Î½Î· ÎŸÎ´Î·Î³Î¯Î±', content: 'Î•Î¹ÏƒÎ±Î³Î¬Î³ÎµÏ„Îµ Ï„Î· Î´Î¹ÎºÎ® ÏƒÎ±Ï‚ Î¿Î´Î·Î³Î¯Î±...' };
        setCurrentPrompt(p => ({ ...p, structure: [...p.structure, nb] }));
    }, [clearMessages]);

    const handleDeleteBlock = useCallback((id) => { clearMessages(); setCurrentPrompt(p => ({ ...p, structure: p.structure.filter(b => b.id !== id) })); }, [clearMessages]);
    const handleUpdateBlockContent = useCallback((id, content) => { clearMessages(); setCurrentPrompt(p => ({ ...p, structure: p.structure.map(b => b.id === id ? { ...b, content } : b) })); }, [clearMessages]);
    const handlePromptNameChange = (e) => setCurrentPrompt(p => ({ ...p, name: e.target.value }));
    const handlePromptDescriptionChange = (e) => setCurrentPrompt(p => ({ ...p, description: e.target.value }));
    const handlePromptPublicToggle = (e) => setCurrentPrompt(p => ({ ...p, is_public: e.target.checked }));

    const handleSavePrompt = useCallback(async () => {
        clearMessages();
        if (!currentPrompt.name.trim()) { showError("Î¤Î¿ ÏŒÎ½Î¿Î¼Î± Ï„Î¿Ï… Î²Î¿Î·Î¸Î¿Ï AI ÎµÎ¯Î½Î±Î¹ Î±Ï€Î±ÏÎ±Î¯Ï„Î·Ï„Î¿."); return; }
        setIsSaving(true);
        const payload = { name: currentPrompt.name.trim(), description: currentPrompt.description.trim(), is_public: currentPrompt.is_public, structure: currentPrompt.structure };
        try {
            let response; let msg = '';
            if (currentPrompt.id) {
                response = await updatePrompt(currentPrompt.id, payload);
                msg = `ÎŸ Î²Î¿Î·Î¸ÏŒÏ‚ "${response.data.name}" ÎµÎ½Î·Î¼ÎµÏÏŽÎ¸Î·ÎºÎµ ÎµÏ€Î¹Ï„Ï…Ï‡ÏŽÏ‚.`;
                setSavedPrompts(prev => prev.map(p => p.id === response.data.id ? { ...p, ...response.data } : p));
                const updatedDetails = await getPromptDetails(currentPrompt.id);
                setCurrentPrompt(prev => ({ ...prev, ...updatedDetails.data }));
            } else {
                response = await savePrompt(payload);
                msg = `ÎŸ Î²Î¿Î·Î¸ÏŒÏ‚ "${response.data.name}" Î´Î·Î¼Î¹Î¿Ï…ÏÎ³Î®Î¸Î·ÎºÎµ ÎµÏ€Î¹Ï„Ï…Ï‡ÏŽÏ‚.`;
                setCurrentPrompt(prev => ({ ...prev, id: response.data.id, ...response.data, system_prompt: response.data.system_prompt }));
                setSavedPrompts(prev => [response.data, ...prev]);
            }
            showSuccess(msg);
        } catch (err) {
            console.error("Error saving prompt:", err);
            showError(err.response?.data?.error || "Î‘Ï€Î¿Ï„Ï…Ï‡Î¯Î± Î±Ï€Î¿Î¸Î®ÎºÎµÏ…ÏƒÎ·Ï‚ Ï„Î¿Ï… Î²Î¿Î·Î¸Î¿Ï AI.");
        } finally { setIsSaving(false); }
    }, [currentPrompt, showError, showSuccess, clearMessages]);

    const handleLoadPrompt = useCallback(async (promptToLoad) => {
        clearMessages();
        if (!promptToLoad?.id) { showError("Î•Ï€Î¹Î»Î­Ï‡Î¸Î·ÎºÎµ Î¼Î· Î­Î³ÎºÏ…ÏÎ¿Ï‚ Î²Î¿Î·Î¸ÏŒÏ‚."); setShowLoadPromptModal(false); return; }
        setLoadingState(s => ({ ...s, promptDetails: true }));
        try {
            const response = await getPromptDetails(promptToLoad.id);
            const fp = response.data;
            setCurrentPrompt({
                id: fp.id, name: fp.name || '', description: fp.description || '',
                is_public: !!fp.is_public,
                structure: Array.isArray(fp.structure) ? fp.structure : [],
                system_prompt: fp.system_prompt || ''
            });
            showSuccess(`Î¦Î¿ÏÏ„ÏŽÎ¸Î·ÎºÎµ Î¿ Î²Î¿Î·Î¸ÏŒÏ‚: "${fp.name}"`);
        } catch (err) { console.error("Error loading prompt details:", err); showError(`Î‘Ï€Î¿Ï„Ï…Ï‡Î¯Î± Ï†ÏŒÏÏ„Ï‰ÏƒÎ·Ï‚ Ï„Î¿Ï… "${promptToLoad.name}".`);
        } finally { setLoadingState(s => ({ ...s, promptDetails: false })); setShowLoadPromptModal(false); }
    }, [showError, showSuccess, clearMessages]);

    const handleNewPrompt = useCallback(() => { clearMessages(); setCurrentPrompt({ id: null, name: '', description: '', is_public: false, structure: [], system_prompt: '' }); showSuccess("ÎŸ ÎºÎ±Î¼Î²Î¬Ï‚ ÎµÎ¯Î½Î±Î¹ Î­Ï„Î¿Î¹Î¼Î¿Ï‚ Î³Î¹Î± Î­Î½Î± Î½Î­Î¿ Î²Î¿Î·Î¸ÏŒ."); }, [clearMessages, showSuccess]);

    const handleDragStart = (event) => { clearMessages(); setActiveId(event.active.id); };
    const handleDragEnd = (event) => { const { active, over } = event; setActiveId(null); if (over && active.id !== over.id) { const oldIdx = currentPrompt.structure.findIndex(i => i.id === active.id); const newIdx = currentPrompt.structure.findIndex(i => i.id === over.id); if (oldIdx !== -1 && newIdx !== -1) { setCurrentPrompt(p => ({ ...p, structure: arrayMove(p.structure, oldIdx, newIdx) })); } } };
    const draggedBlockData = activeId ? currentPrompt.structure.find(b => b.id === activeId) : null;
    const systemPromptForSandbox = currentPrompt.system_prompt ? currentPrompt.system_prompt : currentPrompt.structure.map(block => block.content).join("\n\n");

    const startTutorial = () => { setIsTutorialActive(true); setTutorialStep(0); };
    const nextTutorialStep = () => { if (tutorialStep < guidedSteps.length - 1) setTutorialStep(s => s + 1); else setIsTutorialActive(false); };
    const endTutorial = () => {
        if (highlightedElementRef.current) {
            highlightedElementRef.current.classList.remove('tutorial-highlight');
            highlightedElementRef.current = null;
        }
        setIsTutorialActive(false);
    };

    useEffect(() => {
        if (highlightedElementRef.current) { highlightedElementRef.current.classList.remove('tutorial-highlight'); highlightedElementRef.current = null; }
        if (isTutorialActive && guidedSteps[tutorialStep]) {
            const selector = guidedSteps[tutorialStep].selector;
            try { const element = document.querySelector(selector); if (element) { element.scrollIntoView({ behavior: 'smooth', block: 'center', inline: 'nearest' }); element.classList.add('tutorial-highlight'); highlightedElementRef.current = element; } else { console.warn(`Tutorial: Element "${selector}" not found for step ${tutorialStep}.`); }
            } catch (e) { console.warn(`Tutorial: Invalid selector "${selector}" for step ${tutorialStep}.`); }
        }
        return () => { if (highlightedElementRef.current) { highlightedElementRef.current.classList.remove('tutorial-highlight'); } };
    }, [isTutorialActive, tutorialStep]);

    return (
        <div className="prompt-workshop-page page-content-wrapper">
            <div className="page-header">
                <h2><FaEdit /> Î•ÏÎ³Î±ÏƒÏ„Î®ÏÎ¹ Î•ÎºÏ€Î±Î¯Î´ÎµÏ…ÏƒÎ·Ï‚ Î‘Î™ Î’Î¿Î·Î¸ÏŽÎ½</h2>
                <button onClick={startTutorial} className="button subtle-button">
                    <FaQuestion /> Î“ÏÎ®Î³Î¿ÏÎ· ÎšÎ±Î¸Î¿Î´Î®Î³Î·ÏƒÎ·
                </button>
            </div>

            {(messages.error || messages.success) &&
                <div className={`message global-message ${messages.error ? 'error-message' : 'success-message'}`}>
                    {messages.error || messages.success}
                    <button onClick={clearMessages} title="Î‘Ï€ÏŒÎºÏÏ…ÏˆÎ·">X</button>
                </div>
            }

            <DndContext sensors={sensors} collisionDetection={closestCenter} onDragStart={handleDragStart} onDragEnd={handleDragEnd} measuring={{ droppable: { strategy: MeasuringStrategy.Always } }}>
                <div className="prompt-workshop-container">
                    <div className="workshop-main-area">
                        <aside className="workshop-column workshop-column-palette">
                            <BlockPalette
                                blocksByCategory={initialPreMadeBlocks}
                                onAddBlock={handleAddBlock}
                                onAddCustom={handleAddCustomTextBlock}
                            />
                        </aside>
                        <main className="workshop-column workshop-column-canvas">
                            <div className="prompt-canvas-section widget">
                                <div className="prompt-metadata-controls">
                                    <div className="prompt-meta-row-1">
                                        <input type="text" placeholder="ÎŒÎ½Î¿Î¼Î± Î’Î¿Î·Î¸Î¿Ï AI (Î‘Ï€Î±Î¹Ï„ÎµÎ¯Ï„Î±Î¹)" value={currentPrompt.name} onChange={handlePromptNameChange} className="prompt-name-input" required />
                                        <div className="prompt-actions-main">
                                            <button className="button primary-button" onClick={handleSavePrompt} disabled={isSaving || !currentPrompt.name.trim()}> <FaSave /> {isSaving ? 'Î‘Ï€Î¿Î¸Î®ÎºÎµÏ…ÏƒÎ·...' : (currentPrompt.id ? 'Î•Î½Î·Î¼Î­ÏÏ‰ÏƒÎ·' : 'Î‘Ï€Î¿Î¸Î®ÎºÎµÏ…ÏƒÎ·')} </button>
                                            <button onClick={handleNewPrompt} className="button subtle-button" title="ÎšÎ±Î¸Î±ÏÎ¹ÏƒÎ¼ÏŒÏ‚ & ÎÎ­Î¿Ï‚ Î’Î¿Î·Î¸ÏŒÏ‚"><FaSyncAlt /> ÎÎ­Î¿Ï‚ </button>
                                        </div>
                                    </div>
                                    <div className="prompt-meta-row-2">
                                        <textarea placeholder="Î£ÏÎ½Ï„Î¿Î¼Î· Ï€ÎµÏÎ¹Î³ÏÎ±Ï†Î® Î³Î¹Î± Ï„Î¿Ï…Ï‚ Î¼Î±Î¸Î·Ï„Î­Ï‚ (Ï€ÏÎ¿Î±Î¹ÏÎµÏ„Î¹ÎºÏŒ)..." value={currentPrompt.description} onChange={handlePromptDescriptionChange} className="prompt-description-input" rows={2} />
                                    </div>
                                    <div className="prompt-actions-extra">
                                        <div className="prompt-load-add-buttons">
                                            <button className="button subtle-button" onClick={() => setShowAddMaterialModal(true)} title="Î ÏÎ¿ÏƒÎ¸Î®ÎºÎ· Ï€Î»Î±Î¹ÏƒÎ¯Î¿Ï… Î±Ï€ÏŒ Ï„Î¿ Ï…Î»Î¹ÎºÏŒ ÏƒÎ±Ï‚"><FaFileUpload /> Î ÏÎ¿ÏƒÎ¸Î®ÎºÎ· Î¥Î»Î¹ÎºÎ¿Ï</button>
                                            <button className="button subtle-button" onClick={() => setShowLoadPromptModal(true)} title="Î¦ÏŒÏÏ„Ï‰ÏƒÎ· Î±Ï€Î¿Î¸Î·ÎºÎµÏ…Î¼Î­Î½Î¿Ï… Î²Î¿Î·Î¸Î¿Ï"><FaHistory /> Î¦ÏŒÏÏ„Ï‰ÏƒÎ· Î’Î¿Î·Î¸Î¿Ï</button>
                                        </div>
                                        <div className="prompt-public-toggle">
                                            <input type="checkbox" id={`is_public_toggle_PW_${currentPrompt.id || 'newP'}`} checked={currentPrompt.is_public} onChange={handlePromptPublicToggle} disabled={isSaving} />
                                            <label htmlFor={`is_public_toggle_PW_${currentPrompt.id || 'newP'}`} className={currentPrompt.is_public ? 'is-public' : ''} title="ÎÎ± ÎµÎ¯Î½Î±Î¹ Î´Î¹Î±Î¸Î­ÏƒÎ¹Î¼Î¿Ï‚ ÏƒÏ„Î¿Ï…Ï‚ Î¼Î±Î¸Î·Ï„Î­Ï‚;">
                                                {currentPrompt.is_public ? 'Î”Î·Î¼ÏŒÏƒÎ¹Î¿Ï‚ (Î“Î¹Î± Î¼Î±Î¸Î·Ï„Î­Ï‚)' : 'Î™Î´Î¹Ï‰Ï„Î¹ÎºÏŒÏ‚ (ÎœÏŒÎ½Î¿ Î³Î¹Î± ÎµÎºÏ€Î±Î¹Î´ÎµÏ…Ï„Î¹ÎºÎ¿ÏÏ‚)'}
                                            </label>
                                        </div>
                                    </div>
                                </div>
                                <SortableContext items={currentPrompt.structure.map(b => b.id)} strategy={verticalListSortingStrategy}>
                                    <PromptBuilder blocks={currentPrompt.structure} onDeleteBlock={handleDeleteBlock} onUpdateBlockContent={handleUpdateBlockContent} activeId={activeId} />
                                </SortableContext>
                            </div>
                        </main>
                    </div>

                    <div className="workshop-sandbox-area">
                        <Sandbox
                            currentPromptStructure={currentPrompt.structure}
                            systemPromptForSandbox={systemPromptForSandbox}
                            promptIdToUseIfNoStructure={currentPrompt.id}
                            showError={showError}
                            showSuccess={showSuccess}
                            clearMessages={clearMessages}
                        />
                    </div>
                </div>

                <DragOverlay dropAnimation={null}>
                    {activeId && draggedBlockData ? (<SortableBlock id={draggedBlockData.id} block={draggedBlockData} isDraggingOverlay={true} onDeleteBlock={()=>{}} onUpdateBlockContent={()=>{}} />) : null}
                </DragOverlay>
            </DndContext>

            {showLoadPromptModal && (
                <Modal title="Î¦ÏŒÏÏ„Ï‰ÏƒÎ· Î‘Ï€Î¿Î¸Î·ÎºÎµÏ…Î¼Î­Î½Î¿Ï… Î’Î¿Î·Î¸Î¿Ï" onClose={() => setShowLoadPromptModal(false)} width="700px" showFooter={true}>
                    {loadingState.promptDetails ? <div className="loading-items modal-loading"><FaSpinner className="spin" /> Î›Î®ÏˆÎ· Î»ÎµÏ€Ï„Î¿Î¼ÎµÏÎµÎ¹ÏŽÎ½...</div>
                        : <PromptListForModal prompts={savedPrompts} onLoadPrompt={handleLoadPrompt} isLoading={loadingState.prompts} currentPromptId={currentPrompt.id} showError={showError} />
                    }
                </Modal>
            )}
            {showAddMaterialModal && (
                <Modal title="Î ÏÎ¿ÏƒÎ¸Î®ÎºÎ· Î Î»Î±Î¹ÏƒÎ¯Î¿Ï… Î±Ï€ÏŒ Î¥Î»Î¹ÎºÏŒ" onClose={() => setShowAddMaterialModal(false)} width="700px" showFooter={true}>
                    <MaterialListForModal materials={materials} onMaterialClick={handleAddMaterialSummary} isLoading={loadingState.materials} showError={showError} />
                </Modal>
            )}

            {isTutorialActive && guidedSteps[tutorialStep] && (
                <div className="tutorial-overlay-backdrop" onClick={endTutorial}>
                    <div className="tutorial-tooltip-wrapper" style={{ /* Consider JS positioning */ }}>
                        <div className="tutorial-tooltip" onClick={e => e.stopPropagation()}>
                            <p>{guidedSteps[tutorialStep].content}</p>
                            <div style={{ marginTop: '1em', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                <div>
                                    {tutorialStep > 0 && <button onClick={() => setTutorialStep(s => s-1)} className='button subtle-button small'>Î ÏÎ¿Î·Î³Î¿ÏÎ¼ÎµÎ½Î¿</button>}
                                </div>
                                <span>Î’Î®Î¼Î± {tutorialStep + 1} Î±Ï€ÏŒ {guidedSteps.length}</span>
                                <div>
                                    <button onClick={nextTutorialStep} className='button primary-button small'>
                                        {tutorialStep < guidedSteps.length - 1 ? 'Î•Ï€ÏŒÎ¼ÎµÎ½Î¿' : 'Î¤Î­Î»Î¿Ï‚'}
                                    </button>
                                    {tutorialStep < guidedSteps.length - 1 && <button onClick={endTutorial} className='button subtle-button small close-tutorial'>Î Î±ÏÎ¬Î»ÎµÎ¹ÏˆÎ·</button> }
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default PromptWorkshop;
